var link=document.querySelector(".feedback"),popup=document.querySelector(".container-form"),close=popup.querySelector(".btn-form-close"),form=popup.querySelector("form"),login=popup.querySelector("[name=login]"),email=popup.querySelector("[name=email]"),storage=localStorage.getItem("login");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("container-form-show"),storage?(login.value=storage,email.focus()):login.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("container-form-show")}),form.addEventListener("submit",function(e){login.value&&email.value?localStorage.setItem("login",login.value):(e.preventDefault(),alert("Нужно ввести логин, почту и текст"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("container-form-show")&&popup.classList.remove("container-form-show")});